.futuristic-button {
  background: linear-gradient(to right, #FF00FF, #4B0082);
  color: white;
  padding: 15px 30px;
  border: none;
  border-radius: 5px;
  box-shadow: 0 0 10px #FF00FF, 0 0 20px #4B0082;
  transition: all 0.3s ease;
}
.futuristic-button:hover {
  transform: scale(1.05);
  box-shadow: 0 0 15px #FF00FF, 0 0 30px #4B0082;
}

.glass-card {
  background: rgba(255, 255, 255, 0.2);
  border-radius: 16px;
  box-shadow: 0 4px 30px rgba(0, 0, 0, 0.1);
  backdrop-filter: blur(5px);
  -webkit-backdrop-filter: blur(5px);
  border: 1px solid rgba(255, 255, 255, 0.3);
  padding: 20px;
}

/* Chart specific styles */
#chart-container {
  width: 100%;
  height: 300px;
  border: 1px solid #ccc;
}
