* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  background: radial-gradient(ellipse at center, #0f0f0f 0%, #050505 100%);
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  font-family: 'Orbitron', sans-serif;
  color: #00ffff;
  overflow: hidden;
}

.glitch {
  font-size: 3rem;
  position: relative;
  color: #00ffff;
  animation: flicker 2s infinite;
  text-shadow:
    0 0 5px #0ff,
    0 0 10px #0ff,
    0 0 20px #0ff,
    0 0 40px #0ff,
    0 0 80px #0ff;
}

.glitch::before,
.glitch::after {
  content: attr(data-text);
  position: absolute;
  left: 0;
  width: 100%;
  overflow: hidden;
  color: #ff00c8;
}

.glitch::before {
  animation: glitchTop 1s infinite linear alternate-reverse;
  top: -2px;
}

.glitch::after {
  animation: glitchBottom 1s infinite linear alternate-reverse;
  bottom: -2px;
}

@keyframes glitchTop {
  0% { clip-path: inset(0 0 90% 0); transform: translateX(-2px); }
  50% { clip-path: inset(0 0 85% 0); transform: translateX(2px); }
  100% { clip-path: inset(0 0 90% 0); transform: translateX(-2px); }
}

@keyframes glitchBottom {
  0% { clip-path: inset(85% 0 0 0); transform: translateX(2px); }
  50% { clip-path: inset(90% 0 0 0); transform: translateX(-2px); }
  100% { clip-path: inset(85% 0 0 0); transform: translateX(2px); }
}

@keyframes flicker {
  0%, 18%, 22%, 25%, 53%, 57%, 100% {
    opacity: 1;
  }
  20%, 24%, 55% {
    opacity: 0;
  }
}
